import React from 'react'
import { 
  Box, 
  Stack, 
  styled,
  Button, 
  
} from '@mui/material'

import imgMen from '../assets/men.png';
import fundoAzul from '../assets/fundoAzul.png'




const Information = () => {

    const StackColumn = styled(Stack) (() => ({
      
      display:'flex',
      alignItems: 'center',
      flex: 1,
      gap: 8,
      textAlign: 'center',
      position: 'relative',
     
      
    }));
  
    const BoxRow = styled(Box) (({ theme }) => ({
      display: 'flex',
      flexDirection: 'row',
      paddingBottom: 0,
      backgroundImage: `url(${fundoAzul})`,
      flex: 1,
      [theme.breakpoints.down('sm')]: {
        flexDirection: 'column',
        gap: 30,
      },
      
      
    }));

    const imgStyle = {
      width: '40%', 
      height: 'auto', 
      paddingLeft: '70%',
      
      
      
    }; 

    
  const textStyle = {
    fontSize: '50px', // Ajuste o tamanho do texto aqui
    color: 'white',
    display:"flex",
    
  };

  const buttonStyle = {
    fontSize: '16px', // Ajuste o tamanho do texto dos bot천es aqui
    padding: '12px 24px',

  };
   
  
    return (
      <BoxRow
        component="information"
        sx={{
          py: 4,
          px: 2,
          paddingBottom: 0,
          paddingTop: 0,
        }}
      >
        <StackColumn>
          <h1 style={textStyle}>Seu Texto Aqui</h1>
          <Button variant="contained" color="primary" style={buttonStyle}>
            Bot찾o 1
          </Button>
          <Button variant="outlined" color="secondary" style={buttonStyle}>
            Bot찾o 2
          </Button>
        </StackColumn>
        <div className="container">
          <img
            src={imgMen}
            alt="Homem Apontando"
            className="imagem-direita"
            style={imgStyle}
          />
        </div>
      </BoxRow>
    );
  };
  
  export default Information;
