import React from 'react'
import { 
  Box, 
  Stack, 
  styled,
  Button, 
  
} from '@mui/material'

import imgMen from '../assets/men.png';
import fundoAzul from '../assets/fundoAzul.png'




const Information = () => {

    const StackColumn = styled(Stack) (() => ({
      
      display:'flex',
      alignItems: 'center',
      flex: 1,
      flexDirection: 'column', // Altere a direção para uma coluna
      gap: 8,
      textAlign: 'center',
      position: 'relative',
     
      
    }));
  
    const BoxRow = styled(Box) (({ theme }) => ({
      display: 'flex',
      flexDirection: 'row',
      paddingBottom: 0,
      backgroundImage: `url(${fundoAzul})`,
      flex: 1,
      [theme.breakpoints.down('sm')]: {
        flexDirection: 'column',
        gap: 30,
      },
      
      
    }));

    const imgStyle = {
      width: '40%', 
      maxWidth: '100%', // Defina a largura máxima para ajustar ao tamanho da tela
      height: 'auto', 
      float: 'right',
      
      
      
    }; 
    
    const imgContainerStyle = {
      flex: 1, // A imagem ocupará todo o espaço disponível
      display: 'flex', // Use flex para redimensionar a imagem
      justifyContent: 'flex-end', // Alinhe a imagem à direita
    };

    
  const textStyle = {
    fontSize: '40px', // Ajuste o tamanho do texto aqui
    color: 'white',
    flex: 1, // Expande o texto para preencher o espaço disponível
    marginRight: '20px',
    marginTop: '70px',
    
  };

  const buttonStyle = {
    fontSize: '16px', // Ajuste o tamanho do texto dos botões aqui
    padding: '16px ',
    width: '250px',

  };

  const buttonContainerStyle = {
    display: 'flex', // Use flex para colocar os botões lado a lado
    gap: '30px', // Espaçamento entre os botões
    paddingBottom: '100px',
    flexDirection: 'column', // Defina uma coluna para botões em telas menores
    alignItems: 'center', // Centralize os botões em telas menores
    textAlign: 'center', // Centralize o texto em telas menores
  };
   
  
    return (
      <BoxRow
        component="information"
        sx={{
          py: 4,
          px: 2,
          paddingBottom: 0,
          paddingTop: 0,
        }}
      >
            <StackColumn>
        <h1 style={textStyle}>Dúvidas ou Orçamento? Estamos Aqui para Ajudar! Entre em Contato Conosco.</h1>
        <div style={buttonContainerStyle}>
          <Button variant="contained" color="primary" style={buttonStyle}>
            Whatsapp
          </Button>
          <Button variant="contained" color="primary" style={buttonStyle}>
            Envie mensagem
          </Button>
        </div>
      </StackColumn>
       
      <div style={imgContainerStyle}>
        <img
          src={imgMen}
          alt="Homem Apontando"
          className="imagem-direita"
          style={imgStyle}
        />
      </div>
        
      </BoxRow>
    );
  };
  
  export default Information;
