import React from 'react';
import { Link } from 'react-router-dom';
import Grid from '@mui/material/Grid';

import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

import "../styles/style.css"
import ProductsInfo from './ProductsInfo';


const Products = () => {
  return (
    <div className="product-container">
      {ProductsInfo.map((product, index) => (
        <div key={index} className="product-card">
           
             
            <Grid container spacing={2} className='grid-image'>
            <Grid item xs={6} md={4}>
    
    <img
                src={product.image}
                alt={product.title}
                style={{
                  width: '300px', // Defina a largura desejada aqui
                  height: '200px', // Isso mantém a proporção original da imagem
                  maxWidth: '100%'
                }}
              />
  <Grid item xs={6} md={8}>
  
    <Typography gutterBottom 
            variant="h5" 
            component="div">
              {product.title}
            </Typography>
            <Typography variant="body2" 
            color="text.secondary"
            style={{
              height: '70px',
              overflow: 'hidden',
            }}
            >
              {product.description}
            </Typography>
          
              <Link to={`/contact/`} className="info-button">Orçamento</Link> 
              <Link to={`/product/${product.id}`} className="info-button">Mais Informações</Link> 
    
  </Grid>
  
    
  </Grid>
  </Grid>
            
           
              </div>
        
      ))}
    </div>
  );
};

export default Products;
