import {
    Box,
    Button,
    Stack,
    TextField,
    TextareaAutosize,
  } from '@mui/material';
  import Title from './Title';
  import Paragraph from './Paragraph';
  import emailjs from '@emailjs/browser';
  import React, { useState } from 'react';
  
  const Details = () => {
    const [email, setEmail] = useState(''); // Declare email como um estado
    const [phone, setPhone] = useState(''); // Declare phone como um estado
  
    const handleSubmit = (event) => {
      event.preventDefault();
      
      if (email === '' || phone === '') {
        alert('Preencha todos os campos');
        return;
      }
  
      const templateParams = {
        from_email: email,
        from_phone: phone,
      };
  
      emailjs
        .send('service_e4i3boy', 'template_rv49ngu', templateParams, 'cZ_9OYH87Y9tEeJWx')
        .then(
          (response) => {
            console.log('Email Enviado', response.status, response.text);
            setEmail('');
            setPhone('');
          },
          (err) => {
            console.log('Erro: ', err);
          }
        );
    };
  
    return (
      <Stack
        component='section'
        direction='column'
        justifyContent='center'
        alignItems='center'
        sx={{
          py: 10,
          px: 2,
        }}
      >
        {/* ... Seu código para o título e parágrafo ... */}
        <Box component='form' noValidate onSubmit={handleSubmit} sx={{ mt: 1, py: 2 }}>
          {/* ... Seus campos de entrada de email e telefone ... */}
          <Button
            variant='contained'
            fullWidth
            type='submit'
            size='medium'
            sx={{
              fontSize: '0.9rem',
              textTransform: 'capitalize',
              py: 2,
              mt: 3,
              mb: 2,
              borderRadius: 0,
              backgroundColor: '#14192d',
              '&:hover': {
                backgroundColor: '#1e2a5a',
              },
            }}
          >
            Send
          </Button>
        </Box>
      </Stack>
    );
  };
  
  export default Details;
