import React from 'react';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';

// Importe a imagem da sua empresa ou use um placeholder
import companyImage from '../assets/logo.png';

const Company = () => {
  return (
    <div className="about-container">
      <Grid container spacing={3}>
        <Grid item xs={12} sm={6}>
          <img src={companyImage} alt="Company" className="about-image" />
        </Grid>
        <Grid item xs={12} sm={6}>
          <Typography variant="h4" gutterBottom>
            Sobre Nossa Empresa
          </Typography>
          <Typography variant="body1">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam non velit
            at quam convallis volutpat vitae vel elit. Nulla facilisi. Integer
            venenatis vestibulum orci, et bibendum justo tincidunt sit amet. Vestibulum
            fermentum cursus odio id sagittis.
          </Typography>
          {/* Adicione mais informações sobre a empresa aqui */}
        </Grid>
      </Grid>
    </div>
  );
};

export default Company;
