import React from 'react';
import { useParams } from 'react-router-dom';
import products from '../components/Products';

const ProductDetails = () => {
  // Usando o hook useParams para obter o parâmetro 'id' da URL
  const { id } = useParams();

  // Suponha que 'products' seja a mesma matriz de produtos que você usou em 'Products.js'
  // Certifique-se de importar 'products' aqui

  // Encontre o produto correto com base no 'id' da URL
  const product = products[id];

  // Verifique se o produto foi encontrado
  if (!product) {
    return <div>Produto não encontrado</div>;
  }

  return (
    <div>
    <h2>{product.title}</h2>
    <img src={product.image} alt={product.title} />
    <p>{product.description}</p>
    <h3>Informações Adicionais</h3>
    <ul>
      <li>Preço: R$ {product.price}</li>
      <li>Especificações: {product.specifications}</li>
      <li>Disponibilidade: {product.availability}</li>
    </ul>

    <h3>Imagens Adicionais do Produto</h3>
    <div className="product-images">
      {product.additionalImages.map((image, index) => (
        <img key={index} src={image} alt={`Imagem ${index + 1}`} />
      ))}
    </div>

    {/* Adicione mais informações detalhadas ou imagens conforme necessário */}
  </div>
);
};

export default ProductDetails;
