import React from 'react';
import '../styles/style.css'; 

import { Link } from 'react-router-dom';

import janelaProdutos from '../assets/sacadaComeia.png';
import portaProdutos from '../assets/sacadaComeia2.png';
import sacadaProdutos from '../assets/sacadaProdutos.png';
import espelhoProdutos from '../assets/espelhoProdutos.png';


const Products = () => {
  const products = [
    {
      id: 1,
      name: 'Produto 1',
      image: janelaProdutos,
    },
    {
      id: 2,
      name: 'Produto 2',
      image: portaProdutos,
    },
    {
      id: 3,
      name: 'Produto 3',
      image: sacadaProdutos,
    },
    {
      id: 4,
      name: 'Produto 4',
      image: espelhoProdutos,
    },
    // Add more products as needed
  ];

  return (
    <div className="products-container" style={{ display: 'flex', flexDirection: 'column' }}>
      {products.map((product) => (
        <Link 
        to={`/product/${product.id}`} 
        key={product.id} 
        className="product-box"
        style={{ height: '300px', flexGrow: 1 }}>

          <img src={product.image} alt={product.name} style={{ width: '100%', height: '100%', objectFit: 'cover' }}/>
          <p>{product.name}</p>
          
        </Link>
      ))}
    </div>
  );
};

export default Products;
