import React from 'react';
import { useParams, useHistory } from 'react-router-dom';
import products from './Products';

const ProductDetails = () => {
  const { id } = useParams();
  const history = useHistory();

  // ... seu código ...

  if (!product) {
    // Se o produto não for encontrado, você pode redirecionar para uma página de erro ou para a página de produtos
    history.push('/products'); // ou qualquer outra rota adequada
  }

  return (
    <div>
    <h2>{product.title}</h2>
    <img src={product.image} alt={product.title} />
    <p>{product.description}</p>
    <h3>Informações Adicionais</h3>
    <ul>
      <li>Preço: R$ {product.price}</li>
      <li>Especificações: {product.specifications}</li>
      <li>Disponibilidade: {product.availability}</li>
    </ul>

    <h3>Imagens Adicionais do Produto</h3>
    <div className="product-images">
      {product.additionalImages.map((image, index) => (
        <img key={index} src={image} alt={`Imagem ${index + 1}`} />
      ))}
    </div>

    {/* Adicione mais informações detalhadas ou imagens conforme necessário */}
  </div>
);
};

export default ProductDetails;
