import React from 'react'
import { 
  Box, 
  Stack, 
  styled,
  Typography,  
  
} from '@mui/material'
import Title from './Title'

import imgMen from '../assets/men.png';
import fundoAzul from '../assets/fundoAzul.png'




const Information = () => {

    const StackColumn = styled(Stack) (() => ({
      flexDirection: 'column',
      justifyContent: 'flex-start',
      alignItems: 'center',
      flex: 1,
      gap: 8,
      textAlign: 'center',
      position: 'relative',
     
      
    }));
  
    const BoxRow = styled(Box) (({ theme }) => ({
      display: 'flex',
      flexDirection: 'row',
      paddingBottom: 0,
      backgroundImage: `url(${fundoAzul})`,
      flex: 1,
      [theme.breakpoints.down('sm')]: {
        flexDirection: 'column',
        gap: 30,
      },
      
      
    }));

    const imgStyle = {
      width: '40%', 
      height: 'auto', 
      paddingLeft: '70%',
      paddingBottom: '0'
      
      
      
    }; ;;;

   
   
  
    return (
      
      <BoxRow 
      component = 'information'
      sx={{
        py: 4,
        px: 2,
        paddingBottom:'0',
        paddingTop:'0',
      }}
      >
        <StackColumn>
     
<body>
    <div class="container">
        <img src={imgMen} alt="Homem Apontando" class="imagem-direita" style={imgStyle} />
    </div>
</body>

<Box component='article'
                sx={{
                    px: 4,
                }}
                >
               <Typography 
               variant='h4'
               component='h3'
               sx={{ 
                fontWeight: '700',
                textAlign: textAlign,
   }}
    >
                    text={
                      "Dúvidas ou Orçamento? Estamos Aqui para Ajudar! Entre em Contato Conosco."
                    }
                    textAlign={'start'}
                   </Typography>
                 
                </Box>
        </StackColumn>
      </BoxRow>
    )
  }

  export default Information
