import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import "../styles/style.css"
import ProductsInfo from './ProductsInfo';

const Products = () => {
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  return (
    <div className="product-container">
      {ProductsInfo.map((product, index) => (
        <div key={index} className="product-card" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
          <Grid container spacing={2} className='grid-image'>
            <Grid item xs={6} md={8}>
              <Typography gutterBottom variant="h5" component="div">
                {product.title}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {product.description}
              </Typography>
              {isHovered && (
                <>
                  <Link to={`/contact/`} className="info-button">Orçamento</Link>
                  <Link to={`/product/${product.id}`} className="info-button">Mais Informações</Link>
                </>
              )}
            </Grid>
            <Grid item xs={6} md={4}>
              <img
                src={product.image}
                alt={product.title}
                className={isHovered ? 'hovered-image' : 'normal-image'}
              />
            </Grid>
          </Grid>
        </div>
      ))}
    </div>
  );
};

export default Products;
