import React from 'react'
import { 
  Box, 
  Stack, 
  styled, 
  
} from '@mui/material'

import imgMen from '../assets/men.png';
import fundoAzul from '../assets/fundoAzul.png';




const Information = () => {

    const StackColumn = styled(Stack) (() => ({
      flexDirection: 'column',
      justifyContent: 'flex-start',
      alignItems: 'center',
      flex: 1,
      gap: 8,
      textAlign: 'center',
    }));
  
    const BoxRow = styled(Box) (({ theme }) => ({
      display: 'flex',
      flexDirection: 'row',
      backgroundColor: '#ededed',
      flex: 1,
      [theme.breakpoints.down('sm')]: {
        flexDirection: 'column',
        gap: 30,
      }
    }));

  
    return (
      
      <BoxRow 
      component = 'footer'
      sx={{
        py: 4,
        px: 2,
      }}
      >
        <StackColumn>
<Stack 
        direction='row' 
        width= '70px'
        maxWidth='100%'
        justifyContent='space-between'
        >
          
  
    <div class="container" >
    <img src={fundoAzul} alt="Homem Apontando" class="imagem-direita" style={{  width: '100%', height: '100%', position:'fixed', }}/>
        
  
    </div>
    <img src={imgMen} alt="Homem Apontando" class="imagem-direita" style={{ position: 'relative', width: '900%' ,left: '400px' }}/>

        </Stack>
        </StackColumn>
      </BoxRow>
    )
  }

  export default Information
