import React from'react'


//rotas

import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';

//pages
import Home from './pages/Home';
import About from './pages/About';
import Service from './pages/service';
import Contact from './pages/Contact';
//componentes
import Navbar from './components/Navbar';
import Footer from './components/Footer/Footer';
import Information from './components/Information';


import ProductDetail from './components/ProductsDetail';
import ProductsInfo from './components/ProductsInfo';


function InformationWrapper() {
  // Use o hook useLocation para obter a rota atual
  const location = useLocation();

  // Verifique se a rota atual corresponde à página de detalhes do produto
  const isProductDetailPage = location.pathname.startsWith('/product/');

  // Renderize o componente Information apenas na página de detalhes do produto
  if (isProductDetailPage) {
    return <Information />;
  }

  return null;
}



function App() {
  return (
    <>
      <Router>
        <Navbar />
        <Routes>
          <Route path='/' element={<Home />} />
          <Route path='/about' element={<About />} />
          <Route path='/service' element={<Service />} />
          <Route path='/contact' element={<Contact />} />
          <Route path="/product/:id" element={<ProductDetail products={ProductsInfo} />} />
          

        </Routes>
      </Router>
      <InformationWrapper />
      <Footer />
    </>
  );
}

export default App;
