import React from 'react';
import { Box, Stack, styled } from '@mui/material';
import imgMen from '../assets/men.png';

const Information = () => {
  const StackColumn = styled(Stack)(({ theme }) => ({
    flexDirection: 'column',
    justifyContent: 'flex-start',
    alignItems: 'center',
    flex: 1,
    gap: 8,
    textAlign: 'center',
  }));

  const BoxRow = styled(Box)(({ theme }) => ({
    display: 'flex',
    flexDirection: 'row',
    backgroundColor: '#ededed',
    flex: 1,
    [theme.breakpoints.down('sm')]: {
      flexDirection: 'column',
      gap: 30,
    },
  }));

  const ImageContainer = styled.div`
    position: relative;
    width: 5cm; /* Define a largura desejada */
  `;

  const Image = styled.img`
    width: 100%;
    height: auto;
  `;

  return (
    <BoxRow
      component="footer"
      sx={{
        py: 4,
        px: 2,
      }}
    >
      <StackColumn>
        <Stack
          direction="row"
          width="70px"
          maxWidth="100%"
          justifyContent="space-between"
        >
          <ImageContainer>
            <Image src={imgMen} alt="Homem Apontando" />
          </ImageContainer>
          <p>Conteúdo da página.</p>
        </Stack>
      </StackColumn>
    </BoxRow>
  );
};

export default Information;
