import React from 'react'
import { 
  Box, 
  Stack, 
  styled,
  Button, 
  useMediaQuery,
  
  
} from '@mui/material'

import { createTheme, ThemeProvider } from '@mui/material/styles';




import imgMen from '../assets/men.png';
import fundoAzul from '../assets/fundoAzul.png';




const Information = () => {
  const isMobile = useMediaQuery((theme) => theme.breakpoints.down('sm'));

    const StackColumn = styled(Stack) (() => ({
      
      display:'flex',
      alignItems: 'center',
      flex: 1,
      flexDirection: 'column', // Altere a direção para uma coluna
      gap: 8,
      textAlign: 'center',
      position: 'relative',
     
      
    }));
  
    const BoxRow = styled(Box) (({ theme }) => ({
      display: 'flex',
      flexDirection: isMobile ? 'column' : 'row',
      /*flexDirection: 'row',*/
      paddingBottom: 0,
      backgroundImage: `url(${fundoAzul})`,
      flex: 1,
      [theme.breakpoints.down('sm')]: {
        /*flexDirection: 'column',*/
        gap: isMobile ? 30 : 0,
      },
      
      
    }));

    const theme = createTheme({
      // Defina as configurações do seu tema aqui, como breakpoints, cores, etc.
      breakpoints: {
        values: {
          xs: 0,
          sm: 600,
          md: 960,
          lg: 1280,
          xl: 1920,
        },
      },
      // Outras configurações do tema...
    });

    const imgStyle = {
      width: '40%', 
      height: 'auto', 
      float: 'right',
      
    }; 

    
  const textStyle = {
    fontSize: '40px', // Ajuste o tamanho do texto aqui
    color: 'white',
    flex: 1, // Expande o texto para preencher o espaço disponível
    marginRight: '20px',
    marginTop: '70px',
    
  };

  const buttonStyle = {
    fontSize: '16px', // Ajuste o tamanho do texto dos botões aqui
    padding: '16px ',
    width: '250px',
    

  };

  const buttonContainerStyle = {
    display: 'flex', // Use flex para colocar os botões lado a lado
    gap: '30px', // Espaçamento entre os botões
    paddingBottom: '100px'  
    
  };

  
   
  
    return (
      <ThemeProvider theme={theme}>
      <div className="App">
      <BoxRow
        component="information"
        sx={{
          py: 4,
          px: 2,
          paddingBottom: 0,
          paddingTop: 0,
        }}
      >
            <StackColumn>
        <h1 style={textStyle}>Dúvidas ou Orçamento? Estamos Aqui para Ajudar! Entre em Contato Conosco.</h1>
        <div style={buttonContainerStyle}>
          <Button className='botaoWhats' variant="contained" color="primary" style={buttonStyle}>
            Whatsapp
          </Button>
          <Button className='botaoOrcamento' variant="contained" color="primary" style={buttonStyle}>
            Envie mensagem
          </Button>
        </div>
      </StackColumn>
       
          <img
            src={imgMen}
            alt="Homem Apontando"
            className="imagem-direita"
            style={imgStyle}
          />
        
      </BoxRow>
      </div>
    </ThemeProvider>
    );
  };
  
  export default Information;
