import React from 'react';
import { useParams } from 'react-router-dom';

const ProductDetails = () => {
  // Usando o hook useParams para obter o parâmetro 'id' da URL
  const { id } = useParams();

  // Suponha que 'products' seja a mesma matriz de produtos que você usou em 'Products.js'
  // Certifique-se de importar 'products' aqui

  // Encontre o produto correto com base no 'id' da URL
  const product = products[id];

  // Verifique se o produto foi encontrado
  if (!product) {
    return <div>Produto não encontrado</div>;
  }

  return (
    <div>
      <h2>{product.title}</h2>
      <img src={product.image} alt={product.title} />
      <p>{product.description}</p>
      {/* Adicione mais informações sobre o produto aqui */}
    </div>
  );
};

export default ProductDetails;
